import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { searchSongs, searchAlbums, searchArtists } from '../services/api';
import { Song, Album, Artist } from '../types';
import SearchBar from '../components/SearchBar';
import SongCard from '../components/SongCard';
import AlbumCard from '../components/AlbumCard';
import ArtistCard from '../components/ArtistCard';
import { FaPlay, FaPause } from '../icons';
import { useMusicPlayer } from '../context/MusicPlayerContext';

const SearchPage: React.FC = () => {
  const location = useLocation();
  const query = new URLSearchParams(location.search).get('q') || '';

  const [songResults, setSongResults] = useState<Song[]>([]);
  const [albumResults, setAlbumResults] = useState<Album[]>([]);
  const [artistResults, setArtistResults] = useState<Artist[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [activeTab, setActiveTab] = useState<'songs' | 'albums' | 'artists'>('songs');

  const { 
    currentSong,
    isPlaying,
    playSong,
    pauseSong,
    resumeSong,
    addToQueue
  } = useMusicPlayer();

  useEffect(() => {
    const fetchResults = async () => {
      if (!query) {
        setSongResults([]);
        setAlbumResults([]);
        setArtistResults([]);
        return;
      }

      try {
        setLoading(true);

        // Fetch results in parallel for better performance
        const [songs, albums, artists] = await Promise.all([
          searchSongs(query),
          searchAlbums(query),
          searchArtists(query)
        ]);

        setSongResults(songs);
        setAlbumResults(albums);
        setArtistResults(artists);
      } catch (error) {
        console.error('Error searching:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchResults();
  }, [query]);

  const playAllSongs = () => {
    if (songResults.length > 0) {
      // Play the first song
      playSong(songResults[0]);

      // Add rest to queue
      songResults.slice(1, 10).forEach(song => {
        addToQueue(song);
      });
    }
  };

  const isSearchSongPlaying = isPlaying && currentSong && 
    songResults.some(song => song.id === currentSong.id);

  const toggleSearchPlay = () => {
    if (isSearchSongPlaying) {
      pauseSong();
    } else if (currentSong && songResults.some(song => song.id === currentSong.id)) {
      resumeSong();
    } else {
      playAllSongs();
    }
  };

  const hasResults = songResults.length > 0 || albumResults.length > 0 || artistResults.length > 0;

  return (
    <div className="w-full p-6">
      <div className="mb-8">
        <h1 className="text-2xl font-bold mb-4">Search Music</h1>
        <SearchBar className="max-w-2xl" />
      </div>

      {query && (
        <>
          <h2 className="text-xl font-semibold mb-6">
            {loading ? 'Searching...' : `Results for "${query}"`}
          </h2>

          {!loading && !hasResults && (
            <div className="glass p-10 rounded-lg flex items-center justify-center">
              No results found for "{query}"
            </div>
          )}

          {hasResults && (
            <>
              <div className="mb-6 border-b border-gray-700">
                <nav className="flex space-x-8">
                  <button
                    onClick={() => setActiveTab('songs')}
                    className={`py-3 px-1 relative ${
                      activeTab === 'songs'
                        ? 'text-white border-b-2 border-purple-400'
                        : 'text-gray-400 hover:text-white'
                    }`}
                  >
                    Songs ({songResults.length})
                  </button>
                  <button
                    onClick={() => setActiveTab('albums')}
                    className={`py-3 px-1 relative ${
                      activeTab === 'albums'
                        ? 'text-white border-b-2 border-purple-400'
                        : 'text-gray-400 hover:text-white'
                    }`}
                  >
                    Albums ({albumResults.length})
                  </button>
                  <button
                    onClick={() => setActiveTab('artists')}
                    className={`py-3 px-1 relative ${
                      activeTab === 'artists'
                        ? 'text-white border-b-2 border-purple-400'
                        : 'text-gray-400 hover:text-white'
                    }`}
                  >
                    Artists ({artistResults.length})
                  </button>
                </nav>
              </div>

              {activeTab === 'songs' && songResults.length > 0 && (
                <div className="mb-6">
                  <button
                    onClick={toggleSearchPlay}
                    className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-full font-medium flex items-center hover:opacity-90 transition-all mb-6"
                  >
                    {isSearchSongPlaying ? (
                      <>
                        <FaPause className="mr-2" />
                        Pause
                      </>
                    ) : (
                      <>
                        <FaPlay className="mr-2" />
                        Play All
                      </>
                    )}
                  </button>
                </div>
              )}

              <div className="mt-6">
                {activeTab === 'songs' && (
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
                    {songResults.map((song: Song) => (
                      <SongCard key={song.id} song={song} />
                    ))}
                  </div>
                )}

                {activeTab === 'albums' && (
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
                    {albumResults.map((album: Album) => (
                      <AlbumCard key={album.id} album={album} />
                    ))}
                  </div>
                )}

                {activeTab === 'artists' && (
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6">
                    {artistResults.map((artist: Artist) => (
                      <ArtistCard key={artist.id} artist={artist} />
                    ))}
                  </div>
                )}
              </div>
            </>
          )}
        </>
      )}

      {!query && (
        <div className="glass p-10 rounded-lg flex items-center justify-center">
          Enter a search term to find songs, albums, and artists
        </div>
      )}
    </div>
  );
};

export default SearchPage;
