import React from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import MusicPlayer from '../components/MusicPlayer';
import Queue from '../components/Queue';

const MainLayout: React.FC = () => {
  return (
    <div className="flex h-screen overflow-hidden">
      {/* Left Side Container */}
      <div className="h-full flex flex-col w-64 min-w-64 bg-black/30 backdrop-blur-xl">
        {/* Sidebar Navigation */}
        <div className="flex-shrink-0">
          <Sidebar />
        </div>

        {/* Queue Below Sidebar - Make it fill available space */}
        <div className="flex-grow overflow-auto mx-2 mb-24 mt-4">
          <h2 className="text-white text-xl font-bold px-3 mb-2">Queue</h2>
          <Queue className="h-full" />
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto pb-24">
        <Outlet />
      </div>

      {/* Music Player at bottom */}
      <MusicPlayer />
    </div>
  );
};

export default MainLayout;
