import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaSearch, FaList, FaTrophy } from '../react-icons-fix';

const Sidebar: React.FC = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', icon: <FaHome size={20} />, label: 'Home' },
    { path: '/search', icon: <FaSearch size={20} />, label: 'Search' },
    { path: '/library', icon: <FaList size={20} />, label: 'Library' },
    { path: '/trending', icon: <FaTrophy size={20} />, label: 'Trending' },
  ];

  return (
    <div className="glass-dark w-64 p-6 flex flex-col h-full">
      <div className="mb-10">
        <h1 className="text-2xl font-bold bg-gradient-to-r from-purple-400 to-pink-500 bg-clip-text text-transparent">
          SaavnMusic
        </h1>
      </div>

      <nav className="flex-1">
        <ul className="space-y-3">
          {navItems.map((item) => (
            <li key={item.path}>
              <Link
                to={item.path}
                className={`flex items-center p-3 rounded-lg transition-all ${
                  location.pathname === item.path
                    ? 'bg-white bg-opacity-10 text-white'
                    : 'text-gray-300 hover:bg-white hover:bg-opacity-5'
                }`}
              >
                <span className="mr-3">{item.icon}</span>
                <span>{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      <div className="mt-auto glass p-4 rounded-lg">
        <p className="text-sm text-gray-300 mb-2">No account needed</p>
        <p className="text-xs text-gray-400">
          Enjoy music without signup. Just search and play.
        </p>
      </div>
    </div>
  );
};

export default Sidebar;
