import React, { useEffect, useState } from 'react';
import { FaMusic, FaPlayCircle, FaClock, FaHeart, FaList, FaPlus } from '../icons';
import SongCard from '../components/SongCard';
import { useMusicPlayer } from '../context/MusicPlayerContext';
import { Song, Playlist, Album } from '../types';
import { decodeHtmlEntities, getBestImageUrl, safeParseInt } from '../utils/helpers';
import LibraryService from '../services/libraryService';
import { Link } from 'react-router-dom';

const LibraryPage: React.FC = () => {
  const [likedSongs, setLikedSongs] = useState<Song[]>([]);
  const [savedPlaylists, setSavedPlaylists] = useState<Playlist[]>([]);
  const [customPlaylists, setCustomPlaylists] = useState<any[]>([]);
  const [savedAlbums, setSavedAlbums] = useState<Album[]>([]);
  const [recentlyPlayed, setRecentlyPlayed] = useState<Song[]>([]);
  const { currentSong, queue } = useMusicPlayer();

  // Load data from LibraryService
  useEffect(() => {
    // Load liked songs
    setLikedSongs(LibraryService.getLikedSongs());

    // Load saved playlists
    setSavedPlaylists(LibraryService.getSavedPlaylists());

    // Load custom playlists
    setCustomPlaylists(LibraryService.getCustomPlaylists());

    // Load saved albums
    setSavedAlbums(LibraryService.getSavedAlbums());

    // Load recently played
    setRecentlyPlayed(LibraryService.getRecentlyPlayed());
  }, []);

  // Update recently played when song changes
  useEffect(() => {
    if (currentSong) {
      LibraryService.addToRecentlyPlayed(currentSong);
      setRecentlyPlayed(LibraryService.getRecentlyPlayed());
    }
  }, [currentSong]);

  // Create a new custom playlist
  const handleCreatePlaylist = () => {
    const playlistName = prompt('Enter playlist name:');
    if (playlistName && playlistName.trim()) {
      const newPlaylist = LibraryService.createCustomPlaylist(playlistName.trim());
      setCustomPlaylists(LibraryService.getCustomPlaylists());
    }
  };

  return (
    <div className="w-full p-6">
      <div className="mb-8">
        <h1 className="text-2xl font-bold mb-6">Your Library</h1>

        <div className="glass-dark rounded-lg p-6 mb-8">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div className="mb-4 md:mb-0">
              <h2 className="text-xl font-semibold">Local Music Library</h2>
              <p className="text-gray-400 text-sm mt-1">
                Your music is saved locally in this browser. No account needed.
              </p>
            </div>

            <div className="flex flex-wrap gap-4 text-center">
              <div className="glass rounded-lg p-4 w-32">
                <FaHeart className="text-red-400 text-2xl mx-auto mb-2" />
                <p className="text-sm text-gray-300">{likedSongs.length} Liked</p>
              </div>

              <div className="glass rounded-lg p-4 w-32">
                <FaList className="text-blue-400 text-2xl mx-auto mb-2" />
                <p className="text-sm text-gray-300">{customPlaylists.length + savedPlaylists.length} Playlists</p>
              </div>

              <div className="glass rounded-lg p-4 w-32">
                <FaPlayCircle className="text-purple-400 text-2xl mx-auto mb-2" />
                <p className="text-sm text-gray-300">{recentlyPlayed.length} Played</p>
              </div>

              <div className="glass rounded-lg p-4 w-32">
                <FaClock className="text-pink-400 text-2xl mx-auto mb-2" />
                <p className="text-sm text-gray-300">{queue.length} In Queue</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* My Playlists */}
      <section className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">My Playlists</h2>
          <button 
            onClick={handleCreatePlaylist}
            className="flex items-center text-sm text-purple-400 hover:text-purple-300">
            <FaPlus size={14} className="mr-1" />
            Create Playlist
          </button>
        </div>

        {customPlaylists.length === 0 && savedPlaylists.length === 0 ? (
          <div className="glass p-10 rounded-lg flex flex-col items-center justify-center text-gray-400">
            <FaList className="text-4xl mb-4 opacity-50" />
            <p>You don't have any playlists yet</p>
            <p className="text-sm mt-2">Create a playlist or save one to your library</p>
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
            {customPlaylists.map((playlist) => (
              <Link 
                key={playlist.id} 
                to={`/playlist/${playlist.id}`} 
                className="glass rounded-lg overflow-hidden hover:shadow-lg hover:shadow-purple-500/20 transition-all">
                <div className="relative pt-[100%] bg-gradient-to-br from-purple-900 to-indigo-900">
                  {playlist.image ? (
                    <img 
                      src={getBestImageUrl(playlist.image)} 
                      alt={playlist.name} 
                      className="absolute inset-0 w-full h-full object-cover"
                    />
                  ) : (
                    <div className="absolute inset-0 flex items-center justify-center">
                      <FaMusic className="text-4xl text-purple-200 opacity-50" />
                    </div>
                  )}
                  <div className="absolute bottom-3 left-3 right-3 truncate">
                    <h3 className="text-white font-bold truncate text-sm">{playlist.title || playlist.name}</h3>
                    <p className="text-gray-300 text-xs truncate">
                      {playlist.songs.length} songs • Custom
                    </p>
                  </div>
                </div>
              </Link>
            ))}

            {savedPlaylists.map((playlist) => (
              <Link 
                key={playlist.id} 
                to={`/playlist/${playlist.id}`} 
                className="glass rounded-lg overflow-hidden hover:shadow-lg hover:shadow-purple-500/20 transition-all">
                <div className="relative pt-[100%] bg-gradient-to-br from-blue-900 to-indigo-900">
                  {playlist.image ? (
                    <img 
                      src={getBestImageUrl(playlist.image)} 
                      alt={playlist.name} 
                      className="absolute inset-0 w-full h-full object-cover"
                    />
                  ) : (
                    <div className="absolute inset-0 flex items-center justify-center">
                      <FaMusic className="text-4xl text-blue-200 opacity-50" />
                    </div>
                  )}
                  <div className="absolute bottom-3 left-3 right-3 truncate">
                    <h3 className="text-white font-bold truncate text-sm">{playlist.title || playlist.name}</h3>
                    <p className="text-gray-300 text-xs truncate">
                      {playlist.songs || 0} songs • Saved
                    </p>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* Liked Songs */}
      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Liked Songs</h2>

        {likedSongs.length === 0 ? (
          <div className="glass p-10 rounded-lg flex flex-col items-center justify-center text-gray-400">
            <FaHeart className="text-4xl mb-4 opacity-50" />
            <p>You haven't liked any songs yet</p>
            <p className="text-sm mt-2">Like songs to see them here</p>
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
            {likedSongs.slice(0, 12).map((song) => (
              <SongCard key={song.id} song={song} />
            ))}
          </div>
        )}
      </section>

      {/* Recently Played */}
      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Recently Played</h2>

        {recentlyPlayed.length === 0 ? (
          <div className="glass p-10 rounded-lg flex flex-col items-center justify-center text-gray-400">
            <FaMusic className="text-4xl mb-4 opacity-50" />
            <p>You haven't played any songs yet</p>
            <p className="text-sm mt-2">Play some music to see your history here</p>
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
            {recentlyPlayed.slice(0, 12).map((song) => (
              <SongCard key={song.id} song={song} />
            ))}
          </div>
        )}
      </section>

      {/* Queue */}
      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Coming Up Next</h2>

        {queue.length === 0 ? (
          <div className="glass p-10 rounded-lg flex flex-col items-center justify-center text-gray-400">
            <FaClock className="text-4xl mb-4 opacity-50" />
            <p>Your queue is empty</p>
            <p className="text-sm mt-2">Add songs to your queue to see them here</p>
          </div>
        ) : (
          <div className="glass rounded-lg overflow-hidden divide-y divide-gray-700">
            {queue.map((song, index) => (
              <div key={song.id} className="flex items-center p-4 hover:bg-white hover:bg-opacity-5">
                <div className="w-8 text-center text-gray-400 mr-4">
                  {index + 1}
                </div>

                {song.image && (
                  <img
                    src={getBestImageUrl(song.image)}
                    alt={song.name || song.title || ''}
                    className="h-10 w-10 rounded mr-4 object-cover"
                  />
                )}

                <div className="flex-1 min-w-0">
                  <h3 className="text-sm font-medium text-white truncate">
                    {song.name}
                  </h3>
                  <p className="text-xs text-gray-400 truncate">
                    {song.primaryArtists}
                  </p>
                </div>

                <div className="text-xs text-gray-400 ml-4">
                  {Math.floor(safeParseInt(song.duration) / 60)}:
                  {(safeParseInt(song.duration) % 60).toString().padStart(2, '0')}
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default LibraryPage;
