import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getPlaylistDetails, getPlaylistSongs } from '../services/api';
import { Song, Playlist } from '../types';
import { FaPlay, FaPause, FaMusic, FaArrowLeft } from '../icons';
import { useMusicPlayer } from '../context/MusicPlayerContext';
import { getBestImageUrl } from '../utils/helpers';
import SongCard from '../components/SongCard';

const PlaylistPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [playlist, setPlaylist] = useState<Playlist | null>(null);
  const [songs, setSongs] = useState<Song[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [loadingSongs, setLoadingSongs] = useState(false);
  const [songCount, setSongCount] = useState(0);

  const { 
    currentSong,
    isPlaying,
    playSong,
    pauseSong,
    resumeSong,
    addToQueue
  } = useMusicPlayer();

  useEffect(() => {
    const fetchPlaylistData = async () => {
      if (!id) return;

      try {
        setLoading(true);
        setError(null);

        // Get playlist details first
        const playlistData = await getPlaylistDetails(id);
        setPlaylist(playlistData);

        // Get the total song count
        const totalCount = playlistData?.songCount || playlistData?.count || 0;
        setSongCount(totalCount);
        console.log(`Playlist has ${totalCount} songs according to API metadata`);

        // Then load songs with potential pagination
        setLoadingSongs(true);
        const songsData = await getPlaylistSongs(id);
        console.log(`Loaded ${songsData.length} songs for playlist`);
        setSongs(songsData);

        // If we still have fewer songs than expected, show a message
        if (songsData.length < totalCount && songsData.length > 0) {
          console.log(`Warning: Could only load ${songsData.length} out of ${totalCount} songs`);
          // Set a message for the user
          setError(`Note: Only showing ${songsData.length} out of ${totalCount} songs due to API limitations.`);
        }
      } catch (err) {
        console.error('Error fetching playlist:', err);
        setError('Failed to load playlist. Please try again later.');
      } finally {
        setLoading(false);
        setLoadingSongs(false);
      }
    };

    fetchPlaylistData();
  }, [id]);

  const playAllSongs = () => {
    if (songs.length > 0) {
      // Play the first song
      playSong(songs[0]);

      // Add all remaining songs to queue
      const remainingSongs = songs.slice(1);
      if (remainingSongs.length > 0) {
        // Clear existing queue and add all songs
        remainingSongs.forEach(song => {
          addToQueue(song);
        });

        // Show message if many songs are added
        if (remainingSongs.length > 10) {
          const message = document.createElement('div');
          message.className = 'fixed bottom-24 right-4 glass-dark px-4 py-3 rounded-lg text-white text-sm z-50';
          message.textContent = `Added ${remainingSongs.length} songs to queue`;
          document.body.appendChild(message);

          // Remove message after 3 seconds
          setTimeout(() => {
            document.body.removeChild(message);
          }, 3000);
        }
      }
    }
  };

  const isPlaylistPlaying = isPlaying && currentSong && 
    songs.some(song => song.id === currentSong.id);

  const togglePlaylistPlay = () => {
    if (isPlaylistPlaying) {
      pauseSong();
    } else if (currentSong && songs.some(song => song.id === currentSong.id)) {
      resumeSong();
    } else {
      playAllSongs();
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-6 py-8 flex justify-center items-center min-h-[50vh]">
        <div className="glass p-10 rounded-lg">
          <h2 className="text-xl text-white">Loading playlist...</h2>
        </div>
      </div>
    );
  }

  if (error || !playlist) {
    return (
      <div className="container mx-auto px-6 py-8 flex justify-center items-center min-h-[50vh]">
        <div className="glass p-10 rounded-lg">
          <h2 className="text-xl text-white">{error || 'Playlist not found'}</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-6 py-8">
      <button 
        onClick={() => navigate(-1)} 
        className="flex items-center text-gray-300 hover:text-white mb-6"
      >
        <FaArrowLeft className="mr-2" />
        Back
      </button>

      <div className="flex flex-col md:flex-row gap-8 mb-8">
        <div className="w-full md:w-1/3 lg:w-1/4">
          <div className="glass p-5 rounded-lg overflow-hidden">
            <img 
              src={getBestImageUrl(playlist.image)} 
              alt={playlist.title || playlist.name || 'Playlist'} 
              className="w-full aspect-square object-cover rounded-md mb-4"
              onError={(e) => {
                e.currentTarget.src = 'data:image/png;base64,';
              }}
            />
            <h1 className="text-2xl font-bold text-white mb-2 truncate">
              {playlist.title || playlist.name || 'Playlist'}
            </h1>
            <p className="text-gray-300 mb-4 truncate text-sm">
              {playlist.description || ''}
            </p>
            <div className="flex justify-between text-gray-400 text-sm mb-4">
              <span>
                {songCount > 0 ? (
                  <>{songCount} songs</>
                ) : (
                  <>{songs.length} songs</>
                )}
                {loadingSongs && (
                  <span className="ml-2 inline-block animate-pulse">Loading...</span>
                )}
              </span>
              <span>{playlist.followers ? `${playlist.followers} followers` : ''}</span>
            </div>
            <button
              onClick={togglePlaylistPlay}
              className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-full font-medium flex items-center justify-center hover:opacity-90 transition-all"
            >
              {isPlaylistPlaying ? (
                <>
                  <FaPause className="mr-2" />
                  Pause
                </>
              ) : (
                <>
                  <FaPlay className="mr-2" />
                  Play All
                </>
              )}
            </button>
          </div>
        </div>

        <div className="w-full md:w-2/3 lg:w-3/4">
          <div className="glass p-6 rounded-lg">
            <h2 className="text-xl font-bold mb-4 text-white">
              Songs
              {loadingSongs && (
                <span className="ml-3 text-sm font-normal text-gray-400 animate-pulse">
                  Loading songs...
                </span>
              )}
            </h2>

            {error && songs.length > 0 && (
              <div className="mb-4 p-2 glass-dark rounded-md text-yellow-300 text-sm">
                {error}
              </div>
            )}

            {songs.length === 0 ? (
              <p className="text-gray-400">
                {loadingSongs ? 'Loading songs...' : 'No songs found in this playlist'}
              </p>
            ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                {songs.map((song) => (
                  <SongCard key={song.id} song={song} />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PlaylistPage;
