import React from 'react';
import { useMusicPlayer } from '../context/MusicPlayerContext';
import { Song } from '../types';
import { FaPlay, FaPause, FaTimes } from '../icons';
import { decodeHtmlEntities } from '../utils/helpers';

interface QueueProps {
  isOpen: boolean;
  onClose: () => void;
}

const Queue: React.FC<QueueProps> = ({ isOpen, onClose }) => {
  const { 
    queue, 
    currentSong, 
    isPlaying,
    playSong,
    pauseSong,
    resumeSong,
    removeFromQueue,
    clearQueue
  } = useMusicPlayer();

  if (!isOpen) return null;

  const handlePlaySong = (song: Song) => {
    if (currentSong?.id === song.id) {
      if (isPlaying) {
        pauseSong();
      } else {
        resumeSong();
      }
    } else {
      playSong(song);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 z-50 flex justify-end">
      <div className="w-full max-w-md h-full bg-gray-900 shadow-lg overflow-y-auto">
        <div className="sticky top-0 bg-gray-900 p-4 border-b border-gray-800 flex justify-between items-center">
          <div>
            <h2 className="text-xl font-bold">Queue</h2>
            <p className="text-gray-400 text-sm">{queue.length} songs</p>
          </div>
          <div className="flex space-x-4">
            <button 
              onClick={clearQueue}
              className="text-gray-400 hover:text-white transition-colors text-sm"
            >
              Clear Queue
            </button>
            <button 
              onClick={onClose} 
              className="text-gray-400 hover:text-white transition-colors"
            >
              <FaTimes />
            </button>
          </div>
        </div>

        {currentSong && (
          <div className="p-4 border-b border-gray-800">
            <h3 className="text-sm font-semibold text-gray-400 mb-2">Now Playing</h3>
            <div className="flex items-center p-2 hover:bg-white hover:bg-opacity-5 rounded-md">
              <div 
                className="w-8 h-8 mr-3 flex-shrink-0 cursor-pointer"
                onClick={() => {
                  if (isPlaying) {
                    pauseSong();
                  } else {
                    resumeSong();
                  }
                }}
              >
                {isPlaying ? (
                  <div className="w-full h-full flex items-center justify-center">
                    <div className="flex space-x-1 h-4 items-end justify-center">
                      <div className="w-1 bg-purple-400 h-3 animate-pulse"></div>
                      <div className="w-1 bg-purple-400 h-4 animate-pulse"></div>
                      <div className="w-1 bg-purple-400 h-2 animate-pulse"></div>
                    </div>
                  </div>
                ) : (
                  <FaPlay className="mx-auto my-2" />
                )}
              </div>
              <img 
                src={currentSong.image && currentSong.image.length > 0 
                  ? currentSong.image[currentSong.image.length - 1] 
                  : 'https://via.placeholder.com/50?text=No+Image'} 
                alt={currentSong.name} 
                className="w-12 h-12 rounded object-cover mr-3"
                onError={(e) => {
                  e.currentTarget.src = 'https://via.placeholder.com/50?text=Error';
                }}
              />
              <div className="flex-1 min-w-0">
                <h4 className="text-white text-sm font-medium truncate">
                  {decodeHtmlEntities(currentSong.name)}
                </h4>
                <p className="text-gray-400 text-xs truncate">
                  {decodeHtmlEntities(currentSong.primaryArtists)}
                </p>
              </div>
            </div>
          </div>
        )}

        {queue.length > 0 ? (
          <div className="divide-y divide-gray-800">
            <h3 className="text-sm font-semibold text-gray-400 p-4">Next Up</h3>
            {queue.map((song, index) => (
              <div 
                key={`${song.id}-${index}`}
                className="flex items-center p-4 hover:bg-white hover:bg-opacity-5"
              >
                <div className="w-8 text-center text-gray-400 mr-3">
                  {index + 1}
                </div>
                <img 
                  src={song.image && song.image.length > 0 
                    ? song.image[song.image.length - 1] 
                    : 'https://via.placeholder.com/50?text=No+Image'} 
                  alt={song.name} 
                  className="w-12 h-12 rounded object-cover mr-3 cursor-pointer"
                  onClick={() => handlePlaySong(song)}
                  onError={(e) => {
                    e.currentTarget.src = 'https://via.placeholder.com/50?text=Error';
                  }}
                />
                <div className="flex-1 min-w-0 cursor-pointer" onClick={() => handlePlaySong(song)}>
                  <h4 className="text-white text-sm font-medium truncate">
                    {decodeHtmlEntities(song.name)}
                  </h4>
                  <p className="text-gray-400 text-xs truncate">
                    {decodeHtmlEntities(song.primaryArtists)}
                  </p>
                </div>
                <button 
                  onClick={() => removeFromQueue(index)}
                  className="text-gray-500 hover:text-white p-2"
                >
                  <FaTimes size={14} />
                </button>
              </div>
            ))}
          </div>
        ) : (
          <div className="p-8 text-center text-gray-400">
            Queue is empty. Add songs to your queue.
          </div>
        )}
      </div>
    </div>
  );
};

export default Queue;
