import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getPlaylistDetails } from '../services/api';
import { Song, Playlist } from '../types';
import { FaPlay, FaPause, FaMusic, FaArrowLeft, FaHeart, FaPlus } from '../icons';
import { useMusicPlayer } from '../context/MusicPlayerContext';
import { getBestImageUrl } from '../utils/helpers';
import SongCard from '../components/SongCard';
import usePlaylistSongs from '../hooks/usePlaylistSongs';
import LibraryService from '../services/libraryService';

const PlaylistPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [playlist, setPlaylist] = useState<Playlist | null>(null);
  const [loading, setLoading] = useState(true);
  const [loadingPlaylist, setLoadingPlaylist] = useState(true);
  const [inLibrary, setInLibrary] = useState(false);

  // Use our custom hook to fetch songs
  const { songs, loading: loadingSongs, error: songsError, totalCount } = usePlaylistSongs(id);

  const { 
    currentSong,
    isPlaying,
    playSong,
    pauseSong,
    resumeSong,
    addToQueue,
    addMultipleToQueue
  } = useMusicPlayer();

  useEffect(() => {
    const fetchPlaylist = async () => {
      if (!id) return;

      try {
        setLoadingPlaylist(true);

        // Only get playlist details, songs handled by hook
        const playlistData = await getPlaylistDetails(id);
        setPlaylist(playlistData);

        // Check if playlist is in library
        if (playlistData) {
          setInLibrary(LibraryService.isSavedPlaylist(playlistData.id));
        }
      } catch (err) {
        console.error('Error fetching playlist details:', err);
      } finally {
        setLoadingPlaylist(false);
      }
    };

    fetchPlaylist();
  }, [id]);

  // Update loading state based on both playlist and songs loading
  useEffect(() => {
    setLoading(loadingPlaylist || loadingSongs);
  }, [loadingPlaylist, loadingSongs]);

  const playAllSongs = () => {
    if (songs.length > 0) {
      // Play the first song and add the rest to queue in one operation
      const firstSong = songs[0];
      const remainingSongs = songs.slice(1);

      // Play first song
      playSong(firstSong);

      // Add remaining songs to queue
      if (remainingSongs.length > 0) {
        addMultipleToQueue(remainingSongs);

        // Show a notification about added songs
        const message = document.createElement('div');
        message.className = 'fixed bottom-24 right-4 glass-dark px-4 py-3 rounded-lg text-white text-sm z-50';
        message.textContent = `Added ${remainingSongs.length} songs to queue`;
        document.body.appendChild(message);

        // Remove message after 3 seconds
        setTimeout(() => {
          if (document.body.contains(message)) {
            document.body.removeChild(message);
          }
        }, 3000);
      }
    }
  };

  const isPlaylistPlaying = isPlaying && currentSong && 
    songs.some(song => song.id === currentSong.id);

  const togglePlaylistPlay = () => {
    if (isPlaylistPlaying) {
      pauseSong();
    } else if (currentSong && songs.some(song => song.id === currentSong.id)) {
      resumeSong();
    } else {
      playAllSongs();
    }
  };

  const toggleLibrary = () => {
    if (!playlist) return;

    if (inLibrary) {
      LibraryService.removeSavedPlaylist(playlist.id);
      setInLibrary(false);

      // Show notification
      const message = document.createElement('div');
      message.className = 'fixed bottom-24 right-4 glass-dark px-4 py-3 rounded-lg text-white text-sm z-50';
      message.textContent = `Removed from Library`;
      document.body.appendChild(message);

      setTimeout(() => {
        if (document.body.contains(message)) {
          document.body.removeChild(message);
        }
      }, 2000);
    } else {
      LibraryService.addSavedPlaylist(playlist);
      setInLibrary(true);

      // Show notification
      const message = document.createElement('div');
      message.className = 'fixed bottom-24 right-4 glass-dark px-4 py-3 rounded-lg text-white text-sm z-50';
      message.textContent = `Added to Library`;
      document.body.appendChild(message);

      setTimeout(() => {
        if (document.body.contains(message)) {
          document.body.removeChild(message);
        }
      }, 2000);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-6 py-8 flex justify-center items-center min-h-[50vh]">
        <div className="glass p-10 rounded-lg">
          <h2 className="text-xl text-white">Loading playlist...</h2>
        </div>
      </div>
    );
  }

  if (!playlist) {
    return (
      <div className="container mx-auto px-6 py-8 flex justify-center items-center min-h-[50vh]">
        <div className="glass p-10 rounded-lg">
          <h2 className="text-xl text-white">Playlist not found</h2>
        </div>
      </div>
    );
  }

  // Check if it's a custom playlist from library
  const isCustomPlaylist = id?.startsWith('custom_');

  return (
    <div className="container mx-auto px-6 py-8">
      <button 
        onClick={() => navigate(-1)} 
        className="mb-6 flex items-center text-gray-400 hover:text-white transition-colors"
      >
        <FaArrowLeft className="mr-2" /> Back
      </button>

      <div className="flex flex-col md:flex-row gap-8 mb-10">
        {/* Playlist Cover */}
        <div className="w-full md:w-64 flex-shrink-0">
          <div className="aspect-square bg-gradient-to-br from-purple-900 to-indigo-900 rounded-lg overflow-hidden shadow-xl flex items-center justify-center">
            {playlist.image ? (
              <img 
                src={getBestImageUrl(playlist.image)} 
                alt={playlist.name || playlist.title || 'Playlist'} 
                className="w-full h-full object-cover"
              />
            ) : (
              <FaMusic className="text-6xl text-white opacity-50" />
            )}
          </div>
        </div>

        {/* Playlist Info */}
        <div className="flex-1">
          <div className="flex items-center mb-1">
            <span className="uppercase text-xs font-semibold text-gray-400 tracking-wider">
              {isCustomPlaylist ? 'Custom Playlist' : 'Playlist'}
            </span>
          </div>

          <h1 className="text-4xl md:text-5xl font-bold mb-3 text-white">
            {playlist.title || playlist.name}
          </h1>

          {playlist.description && (
            <p className="text-gray-300 mb-4 max-w-2xl">
              {playlist.description}
            </p>
          )}

          <div className="flex items-center text-sm text-gray-400 mb-6">
            <span className="mr-4">{totalCount || songs.length} songs</span>
            {playlist.followers && <span className="mr-4">{playlist.followers} followers</span>}
          </div>

          <div className="flex flex-wrap gap-4">
            <button 
              onClick={togglePlaylistPlay}
              className="flex items-center justify-center bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-500 hover:to-indigo-500 text-white px-6 py-3 rounded-full font-semibold transition-all"
            >
              {isPlaylistPlaying ? <FaPause className="mr-2" /> : <FaPlay className="mr-2" />}
              {isPlaylistPlaying ? 'Pause' : 'Play'}
            </button>

            <button 
              onClick={toggleLibrary}
              className={`flex items-center justify-center border ${inLibrary ? 'border-purple-500 text-purple-400' : 'border-gray-600 text-gray-300'} hover:border-purple-500 hover:text-purple-400 px-6 py-3 rounded-full font-semibold transition-all`}
            >
              {inLibrary ? 'Saved to Library' : 'Add to Library'}
            </button>
          </div>
        </div>
      </div>

      {/* Song List */}
      {songsError && (
        <div className="glass-dark p-4 rounded-lg mb-6 text-amber-300 text-sm">
          {songsError}
        </div>
      )}

      {songs.length === 0 ? (
        <div className="glass p-10 rounded-lg flex flex-col items-center justify-center text-gray-400">
          <FaMusic className="text-4xl mb-4 opacity-50" />
          <p>No songs found in this playlist</p>
        </div>
      ) : (
        <div className="glass rounded-lg overflow-hidden">
          <div className="flex items-center p-4 text-gray-400 text-sm border-b border-gray-800">
            <div className="w-8 mr-4">#</div>
            <div className="w-10 mr-4"></div>
            <div className="flex-1 min-w-0 mr-4">Title</div>
            <div className="w-1/4 hidden md:block">Artist</div>
            <div className="w-20 text-right">Duration</div>
            <div className="w-10"></div>
          </div>

          {songs.map((song, index) => (
            <div 
              key={song.id}
              className={`flex items-center p-4 hover:bg-white hover:bg-opacity-5 transition-colors ${currentSong?.id === song.id ? 'bg-white bg-opacity-10' : ''}`}
            >
              <div className="w-8 text-center text-gray-400 mr-4">
                {currentSong?.id === song.id && isPlaying ? (
                  <div className="flex justify-center items-center h-5">
                    <span className="block w-1 h-3 mx-[1px] bg-purple-500 animate-music-play-1"></span>
                    <span className="block w-1 h-5 mx-[1px] bg-purple-500 animate-music-play-2"></span>
                    <span className="block w-1 h-4 mx-[1px] bg-purple-500 animate-music-play-3"></span>
                  </div>
                ) : (
                  index + 1
                )}
              </div>

              <div className="w-10 mr-4">
                {song.image && (
                  <img 
                    src={getBestImageUrl(song.image)} 
                    alt={song.name || ''} 
                    className="w-10 h-10 object-cover rounded"
                  />
                )}
              </div>

              <div 
                className="flex-1 min-w-0 mr-4 cursor-pointer"
                onClick={() => playSong(song)}
              >
                <h3 className={`font-medium truncate ${currentSong?.id === song.id ? 'text-purple-400' : 'text-white'}`}>
                  {song.name}
                </h3>
                {!song.primaryArtists && song.album && (
                  <p className="text-xs text-gray-400 truncate">
                    {song.album}
                  </p>
                )}
              </div>

              <div className="w-1/4 hidden md:block text-gray-400 text-sm truncate">
                {song.primaryArtists || 'Unknown Artist'}
              </div>

              <div className="w-20 text-right text-gray-400 text-sm">
                {song.duration ? `${Math.floor(parseInt(song.duration) / 60)}:${(parseInt(song.duration) % 60).toString().padStart(2, '0')}` : '-:--'}
              </div>

              <div className="w-10 flex justify-end">
                <button 
                  onClick={() => addToQueue(song)}
                  className="text-gray-400 hover:text-white transition-colors"
                  title="Add to queue"
                >
                  <FaPlus size={16} />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PlaylistPage;
