/**
 * Decodes HTML entities in a string
 * @param text The text to decode
 * @returns The decoded text
 */
export const decodeHtmlEntities = (str: string): string => {
  if (!str) return '';
  let txt = document.createElement('textarea');
  txt.innerHTML = str;
  return txt.value;
};

export const getProxiedImageUrl = (url: any): string => {
  // Return placeholder if url is falsy
  if (!url) return 'https://via.placeholder.com/200?text=No+Image';

  // Ensure url is a string
  const urlStr = String(url);

  // If it's already a data URI, return as is
  if (urlStr.startsWith('data:')) return urlStr;

  // If it's a relative URL, return as is
  if (urlStr.startsWith('/')) return urlStr;

  // For HTTP URLs that don't have HTTPS, convert to HTTPS
  if (urlStr.startsWith('http:')) {
    return urlStr.replace('http:', 'https:');
  }

  // Use imgproxy.io as a fallback (a free image proxy service)
  // Or return the URL directly if it's already HTTPS
  return urlStr.startsWith('https:') 
    ? urlStr 
    : `https://images.weserv.nl/?url=${encodeURIComponent(urlStr)}`;
};
