import React from 'react';
import { Link } from 'react-router-dom';
import { FaCheckCircle } from '../icons';
import { Artist } from '../types';

interface ArtistCardProps {
  artist: Artist;
}

const ArtistCard: React.FC<ArtistCardProps> = ({ artist }) => {
  return (
    <Link 
      to={`/artist/${artist.id}`}
      className="glass p-3 rounded-lg flex flex-col items-center overflow-hidden transition-transform hover:scale-105"
    >
      <div className="relative">
        <img 
          src={artist.image[artist.image.length - 1] || artist.image[0]} 
          alt={artist.name}
          className="w-32 h-32 rounded-full object-cover mb-3" 
        />
        {artist.isVerified && (
          <div className="absolute bottom-3 right-0 text-blue-400">
            <FaCheckCircle size={18} />
          </div>
        )}
      </div>

      <div className="text-center overflow-hidden w-full">
        <h3 className="text-sm font-medium text-white truncate">{artist.name}</h3>
        <p className="text-xs text-gray-400">
          {parseInt(artist.followerCount).toLocaleString()} followers
        </p>
      </div>
    </Link>
  );
};

export default ArtistCard;
